import React, { useState } from 'react';
import './App.css';
import NavBar from './Components/NavBar';

import WelcomeMessage from './Components/WelcomeMessages';

import VideoProcessor from './Components/stick_vid';

import Footer from './Components/Footer';

import ImageVideoProcessor from './Components/imageVideoUpload';

// import VideoProcessingComponent from './Components/videoProcessing';

// import { MessageCircle } from 'lucide-react'; // Import the MessageCircle icon from lucide-react

function App() {
  const [activeTab, setActiveTab] = useState('home');

  const handleNavClick = (tabId) => {
    setActiveTab(tabId);
  };

  return (
    <div className="app">
      <NavBar onNavClick={handleNavClick} activeTab={activeTab} />
      <main className="main-content">
        {activeTab === 'home' && <WelcomeMessage />}
        {activeTab === 'document-upload' && <VideoProcessor />}
        {activeTab === 'image-video-upload' && <ImageVideoProcessor />}

      </main>
      <div className="chat-icon-container">

      </div>
      <Footer />
    </div>
  );
}

export default App;
