import React, { useState } from 'react';
import axios from 'axios';
import ReactPlayer from 'react-player';
import './stick_vid.css';
import logo1 from './sbl_logo.png';
import logo2 from './lf.png';

const VideoProcessor = () => {
  const [inputText, setInputText] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [processedVideoUrl, setProcessedVideoUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleTextSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    setVideoUrl('');
    setProcessedVideoUrl('');

    const formData = new FormData();
    formData.append('text', inputText);

    try {
      // const response = await fetch('http://localhost:8000/process_text', {
        const response = await fetch('http://localhost:5000/process_text', {
        method: 'POST',
        body: formData,
        //  mode: 'no-cors'
      });
      console.log(response)
      console.log(response.status)
      // console.log(response.json())
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      // const fullVideoUrl = `http://localhost:8000${data.video_url}`;
      const fullVideoUrl = `http://localhost:5000${data.video_url}`;
      setVideoUrl(fullVideoUrl);
      console.log("Received video URL:", fullVideoUrl);
    } catch (err) {
      setError(`Error processing text: ${err.message}`);
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleVideoProcess = async () => {
    setIsLoading(true);
    setError('');
    setProcessedVideoUrl('');

    const formData = new FormData();
    formData.append('text', inputText);

    try {
      // const response = await fetch('http://localhost:8000/process_video', {
        const response = await fetch('http://localhost:5000/process_video', {
        method: 'POST',
        body: formData,
        // mode: 'no-cors',

      });
      console.log(response)
      console.log(response.status)
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      // const fullVideoUrl = `http://localhost:8000${data.video_url}`;
      const fullVideoUrl = `http://localhost:5000${data.video_url}`;
      setProcessedVideoUrl(fullVideoUrl);
      console.log("Received video URL:", fullVideoUrl);
    } catch (err) {
      setError(`Error processing video: ${err.message}`);
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  //   try {
  //     const response = await axios.post('http://localhost:8000/process_video');
  //     const fullVideoUrl = `http://localhost:8000${response.data.video_url}`;
  //     setProcessedVideoUrl(fullVideoUrl);
  //     console.log("Received processed video URL:", fullVideoUrl);
  //   } catch (err) {
  //     setError(`Error processing video: ${err.response?.data?.detail || err.message}`);
  //     console.error(err);
  //   } finally {
  //     setIsLoading(false);
  //   }
  // };

  return (
    <div className="video-processor">
      <div className="logo-container">
      <img src={logo1} alt="SBL Logo" style={{ height: '50px', width: '50px' }} />
      <img src={logo2} alt="LF Logo" style={{ height: '50px', width: '50px' }} />

       </div>

      <h1 className="main-title">
        <span className="icon">üé•</span>
        Sign LLM
      </h1>
      <form onSubmit={handleTextSubmit} className="text-input-form">
        <div className="textarea-container">
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            placeholder="Enter text to process"
            className="text-input"
          />
        </div>
        <button 
          type="submit" 
          disabled={isLoading}
          className={`submit-button ${isLoading ? 'uploading' : ''}`}
        >
          {isLoading ? 'Processing...' : 'Process Text'}
        </button>
      </form>

      {error && <p className="status-message error">{error}</p>}

      {videoUrl && (
        <div className="video-section">
          <h2 className="section-title">
            <span className="icon">üìΩÔ∏è</span>
            Pose Video
          </h2>
          <ReactPlayer 
            url={videoUrl} 
            controls={true} 
            width="100%"
            height="auto"
            onError={(e) => {
              console.error("Video playback error:", e);
              setError(`Failed to load video. Please check the URL and ensure the video file exists. Error: ${e.message}`);
            }}
          />
          <button 
            onClick={handleVideoProcess}
            disabled={isLoading}
            className={`submit-button ${isLoading ? 'uploading' : ''}`}
          >
            {isLoading ? 'Processing...' : 'Process Video'}
          </button>
        </div>
      )}

      {processedVideoUrl && (
        <div className="video-section">
          <h2 className="section-title">
            <span className="icon">üéûÔ∏è</span>
            Processed Video
          </h2>
          <ReactPlayer 
            url={processedVideoUrl} 
            controls={true} 
            width="100%"
            height="auto"
            onError={(e) => {
              console.error("Processed video playback error:", e);
              setError(`Failed to load processed video. Please check the URL and ensure the video file exists. Error: ${e.message}`);
            }}
          />
        </div>
      )}
    </div>
  );
};

export default VideoProcessor;


